mapper.select:
SELECT M_NO, M_NM
FROM M
WHERE M_ID = #{mId}
AND M_PW = #{mPw}

SELECT O.OB_NO, O.OB_CON, O.M_NO,O.M_NM FROM (SELECT O.OB_NO,O.OB_CON,M.M_NO,M.M_NM, ROW_NUMBER() OVER(ORDER BY O.OB_NO DESC) AS RNUM FROM OB O INNER JOIN M M ON O.M_NO = M.M_NO WHERE O.OB_DEL = 1
AND M.M_NM LIKE '%' || #{searchTxt} || '%'
AND O.OB_CON LIKE '%' || #{searchTxt} || '%'
) O WHERE O.RNUM BETWEEN #{startCnt} AND #{endCnt}

mapper.update:
UPDATE OB SET OB_CON = #{obCon}
WHERE OB_NO = #{obNo}
AND M_NO =#{mNo}
