mapper.select:
SELECT B.B_NO, B.B_TITLE,B.B_WRITER,B.B_DT,B.B_FILE FROM (SELECT B_NO, B_TITLE, B_WRITER, TO_CHAR(B_DT, 'YYYY-MM-DD')AS B_DT,B_FILE, ROW_NUMBER() OVER(ORDER BY B_NO DESC) AS RNUM FROM B WHERE 1 = 1
AND B_TITLE LIKE '%' || #{searchTxt} || '%'
AND B_WRITER LIKE '%' || #{searchTxt} || '%'
AND B_NO LIKE '%' || #{searchTxt} || '%'
) B WHERE B.RNUM BETWEEN #{startCnt} AND #{endCnt}

SELECT B_NO, B_TITLE, B_WRITER, B_CON, TO_CHAR(B_DT, 'YYYY--MM--DD') AS B_DT,B_FILE
FROM B
WHERE B_NO = #{bNo}
